import { useScrollTrigger } from "@mui/material";
import axios from "axios";
import { useFormik } from "formik";
import { useState } from "react";
import { Link,useNavigate } from "react-router-dom";

export function TodoRegister(){
    let navigate = useNavigate();
    const[msg,setMsg] = useState('');
    const[users,setUsers] = useState([{UserId:"",UserName:"",Email:"",Password:"",Mobile:""}]);
    const[errorClass,setErrorClass] =useState('');


    const formik = useFormik({
        initialValues:{
            UserId:"",
            UserName:"",
            Password:"",
            Email:"",
            Mobile:""

        },
        onSubmit:(user)=>{
            axios.post("http://127.0.0.1:1122/register-user",user)
            .then(()=>{
                alert("Registerd succesfully");
                navigate('/user-login');
            })
        }
    })

    function VerifyUserId(){
        axios.get(`http://127.0.0.1:1122/users`)
        .then(response=>{
            for(var user of response.data){
                if(user.UserId === e.target.value){
                    setMsg('User Id Taken')
                }
            }
        })
    }


    return(
        <div className="d-flex justify-content-center align-items-center p-5 " style={{ height: "100vh",boxShadow:"5px 5px black" }}>
            <div className="w-25 bg-light p-5 m-4 rounded shadow">
            <form onSubmit={formik.handleSubmit}  >
                <h2>Register User</h2>
                <dl>
                    <dt>UserId</dt>
                    <dd><input onChange={formik.handleChange} name="UserId" className="form-control" type="text" /></dd>
                    <dt>User Name</dt>
                    <dd> <input type="text" onChange={formik.handleChange} className="form-control" name="UserName" /></dd>
                    <dt>Password</dt>
                    <dd><input type="text" onChange={formik.handleChange} className="form-control" name="Password" /></dd>
                    <dt>Email</dt>
                    <dd><input type="text" className="form-control" name="Email" onChange={formik.handleChange}  /></dd>
                    <dt>Mobile</dt>
                    <dd><input type="text" name="Mobile" className="form-control"onChange={formik.handleChange}  /></dd>

                </dl>
                <button type="submit" className="w-100 btn btn-dark">Register</button>
            </form>
            <Link to="/user-login" >Existing User?Login</Link>
            </div>
        </div>
    )
}
