import axios from "axios";
import { useFormik } from "formik";
import { validateYupSchema } from "formik";
import { useCookies } from "react-cookie";
import {Link, useNavigate } from "react-router-dom";


export function TodoLogin(){
    const[cookies,setCookie,removeCookie] = useCookies(['userid']);

    let navigate = useNavigate();

    const formik = useFormik({
        initialValues:{
            UserId:"",
            Password:""
        },
        onSubmit:(user)=>{
            axios.get("http://127.0.0.1:1122/users")
            .then(response=>{
                var userdetail = response.data.find(u=> u.UserId===user.UserId);
                if(userdetail){
                    if(userdetail.Password ===user.Password){
                        setCookie('userid',user.UserId);
                        navigate('/user-dash');
                    }else{
                        alert("Invalid Password");
                    }
                }else{
                    alert("Invalid User Id");
                }
            })
        }
    })


    return(
        <div className=" w-25 bg-light p-5  m-4  justify-content-center align-items ">

            <form className="justify-content-center" onSubmit={formik.handleSubmit}>
                <h3>User Login</h3>
                <dl>
                    <dt>UserId</dt>
                    <dd><input onChange={formik.handleChange} className="form-control" type="text" name="UserId" /></dd>
                    <dt>Password</dt>
                    <dd><input onChange={formik.handleChange} name="Password" type="text" className="form-control" /></dd>
                </dl>
                <button type="submit" className="btn btn-dark w-100">Login</button>
            </form>
            <Link to="/user-register" >New User?Register</Link>
        </div>
    )
}
