import axios from "axios";
import { useFormik } from "formik";
import { useEffect, useState } from "react";
import { useCookies } from "react-cookie";
import { useNavigate, useParams, Link } from "react-router-dom";

export function ToDoEditAppointment() {
  const params = useParams();
  const navigate = useNavigate();
  const [appointment, setAppointments] = useState(null);
  const [cookies] = useCookies(['userid']);

  const formatDate = (date) => new Date(date).toISOString().split("T")[0];

  useEffect(() => {
    axios.get(`http://127.0.0.1:1122/get-appointment/${params.id}`)
      .then(response => {
        setAppointments(response.data);
      })
      .catch(error => {
        console.error('Error fetching appointment:', error);
      });
  }, [params.id]);

  const formik = useFormik({
    initialValues: {
      AppointmentId: appointment?.AppointmentId || '',
      Title: appointment?.Title || '',
      Description: appointment?.Description || '',
      Date: appointment ? formatDate(appointment.Date) : '',
      UserId: cookies['userid'] || ''
    },
    onSubmit: (task) => {
      axios.put(`http://127.0.0.1:1122/edit-appointment/${params.id}`, task)
        .then(() => {
          alert("Appointment Modified Successfully...");
          navigate(`/todo-details/${params.id}`);
        })
        .catch(error => {
          console.error("Error modifying appointment:", error);
        });
    },
    enableReinitialize: true
  });

  if (!appointment) return <div>Loading...</div>;

  return (
    <div className="bg-light m-2 p-2">
      <h2>Edit Appointment</h2>
      <form onSubmit={formik.handleSubmit}>
        <dl>
          <dt>Appointment Id</dt>
          <dd><input type="text" name="AppointmentId" onChange={formik.handleChange} value={formik.values.AppointmentId} /></dd>
          <dt>Title</dt>
          <dd><input type="text" name="Title" onChange={formik.handleChange} value={formik.values.Title} /></dd>
          <dt>Description</dt>
          <dd><textarea name="Description" value={formik.values.Description} onChange={formik.handleChange}></textarea></dd>
          <dt>Date</dt>
          <dd>
            <input type="date" name="Date" value={formik.values.Date} onChange={formik.handleChange} />
          </dd>
        </dl>
        <button type="submit" className="btn btn-success">Save</button>
        <Link to={`/todo-details/${params.id}`} className="btn btn-danger ms-2">Cancel</Link>
      </form>
    </div>
  );
}
