import { useEffect, useState } from "react"
import { useNavigate, useParams } from "react-router-dom";

export function ToDoDetails(){
    const[appointment,setAppointments] = useState([{AppointmentId:0,Title:"",Description:"",Date:"",UserId:""}]);
    let params = useParams();
    let navigate = useNavigate();


    useEffect(()=>{
        axios.get(`http://127.0.0.1:1122/get-appointment/${params.id}`)
        .then(response=>{
            setAppointments(response.data)
        })

    },[])

    function handleDeleteClik(){
        var flag = window.confirm(`Are you sure`)
    }




    return(
        <div className="m-2 p-2 bg-light ">
            <h3>Appointment Details</h3>
            <dl>
                <dt>Title</dt>
                <dd>{appointment[0].Title}</dd>
                <dt>Description</dt>
                <dd>{appointment[0].Description}</dd>
                <dt>Date</dt>
                <dd>{appointment[0].Date.format('dddd DD MMM yyyy')}</dd>
            </dl>

            <div>
                <button onClick={handleDeleteClik} className="bi bi-trash-fill btn btn-danger" >Delete</button>
               <Link to={`/todo-edit/${appointment[0].AppointmentId}`} className="bi bi-pen-fill btn btn-warning ms-2 " >Edit</Link>
            </div>
            <Link to="/user-dash" >Back to Appointments</Link>
        </div>
    )
}
