import axios from "axios";
import { useFormik } from "formik";
import { useEffect, useState } from "react";
import { useCookies } from "react-cookie";
import { useNavigate, useParams ,Link} from "react-router-dom";
import moment from "moment";


export function ToDoEditAppointment(){
    let params = useParams();
    let navigate = useNavigate();
    const[appointment,setAppointments] = useState([{AppointmentId:0,Title:"",Description:"",Date:"",UserId:""}]);
    const[cookies,setCookie,removeCookie] = useCookies(['userid']);

    const formik = useFormik({
        initialValues:{
            AppointmentId:appointment[0].AppointmentId,
            Title : appointment[0].Title,
            Description:appointment[0].Description,
            Date:appointment[0].Date,
            UserId:cookies['userid']
        },
        onSubmit:(task)=>{
            axios.put(`http://127.0.0.1:1122/edit-appointment/${params.id}`,task)
            .then(()=>{
                alert("Appointment Modidfied Succesfully...");
                navigate(`/todo-details/${params.id}`)
            })
        },
        enableReinitialize:true
    });

    useEffect(()=>{
        axios.get(`http://127.0.0.1:1122/get-appointment/${params.id}`)
        .then(response=>{
            setAppointments(response.data)
        })
    },[])



    return(
        <div className="bg-light m-2 p-2">
            <h2>Edit Appointment</h2>
            <form onSubmit={formik.handleSubmit} >
                <dl>
                    <dt>Appointment Id</dt>
                    <dd><input type="text" name="AppointmentId" onChange={formik.handleChange} value={formik.values.AppointmentId} /></dd>
                    <dt>Title</dt>
                    <dd><input type="text"name="Title" onChange={formik.handleChange} value={formik.values.Title} /></dd>
                    <dt>Description</dt>
                    <dd>
                    <textarea name="Description" value={formik.values.Description}  onChange={formik.handleChange} ></textarea>
                    </dd>
                    <dt>Date</dt>
                    <dd>
                        <input type="date" name="Date" value={formik.values.Date.slice(0,formik.values.Date.indexOf("T"))} onChange={formik.handleChange} />
                    </dd>
                    
                </dl>
                <button type="submit" className="btn btn-success" >Save</button>
                <Link to={`/todo-details/${params.id}`} className="btn btn-danger  ms-2" >Cancel</Link>
                
            </form>



        </div>
    )
}
