import axios from "axios";
import { useEffect, useState } from "react";
import { Cookies, useCookies } from "react-cookie";
import { useNavigate ,Link} from "react-router-dom";







export function ToDoUserDashBoard(){


    const[cookies,setCookie,removieCookie] = useCookies([['userid']]);
    const[appointments,setAppointments] = useState([{AppointmentId:0,Title:"",Description:"",Date:"",UserId:""}]);

    let navigate = useNavigate()

    function handleSignout(){
        removieCookie(['userid']);
        navigate("/user-login")    
    }

    useEffect(()=>{
        axios.get(`http://127.0.0.1:1122/get-appointments/${cookies['userid']}`)
        .then(response=>{
            setAppointments(response.data)
        })
    },[])




    return(
        <div className="container-fluid">
            <div className="h3 alert alert-light d-flex justify-content-between fw-bold  p-2 mx-3">
                <span>User Dashboard</span>
                <span>Hello! {cookies['userid']}</span>
                <button onClick={handleSignout} className="btn btn-link ">Signout</button>
        
            </div>

            <div>
                <Link to="/add-appointment" className="bi btn btn-primary tex mx-3 p-2 my-2 bi-calendar-check">Add Appointment</Link>
            </div>

            <div className="d-flex">
                {
                    appointments.map(appointment=>
                    <div key={appointment.AppointmentId} className="alert alert-success m-4">
                        <div className="fs-4">{appointment.Title}</div>
                        <div className="my-2">
                            <Link to={`/tod-deatils/${appointment.AppointmentId}`}  className="bi bi-eye-fill btn btn-success me-2">View</Link>
                        </div>
                    </div>)
                }
            </div>


        </div>
    )
}
