import axios from "axios";
import { useFormik } from "formik";
import { validateYupSchema } from "formik";
import { useCookies } from "react-cookie";
import {Link, useNavigate } from "react-router-dom";


export function TodoLogin(){
    const[cookies,setCookie,removeCookie] = useCookies(['userid']);

    let navigate = useNavigate();

    const formik = useFormik({
        initialValues:{
            UserId:"",
            Password:""
        },
        onSubmit:(user)=>{
            axios.get("http://127.0.0.1:1122/users")
            .then(response=>{
                var userdetail = response.data.find(u=> u.UserId===user.UserId);
                if(userdetail){
                    if(userdetail.Password ===user.Password){
                        setCookie('userid',user.UserId);
                        navigate('/user-dash');
                    }else{
                        alert("Invalid Password");
                    }
                }else{
                    alert("Invalid User Id");
                }
            })
        }
    })


    return(
        <div className=" w-25 bg-light p-5  m-4 ">

            <div className=" d-flex justify-content-center align-items-center align-content"></div>
        </div>
    )
}
